INSERT INTO `tb_elemen_kompetensi` (`id`, `id_kompetensi`, `judul_kriteria`, `kriteria_kerja`) VALUES
(1, 1, 'Melakukan analisis jabatan', '- Metode dan perangkat analisis jabatan ditetapkan dalam bentuk dokumen tertulis.\r\n- Informasi pekerjaan dari setiap jabatan diidentifikasi dari berbagai narasumber di dalam organisasi. \r\n- Data dan informasi jabatan dianalisis berdasarkan metode dan perangkat analisis jabatan yang ditetapkan. '),
(2, 1, 'Menetapkan uraian jabatan', '- Komponen uraian jabatan ditentukan sesuai format job description yang telah ditetapkan.\r\n- Uraian jabatan setiap posisi jabatan dibuat berdasarkan data dan informasi jabatan yang telah dikumpulkan.\r\n- Uraian jabatan ditetapkan berdasarkan Standar Operasional Prosedur (SOP) yang berlaku di organisasi.'),
(3, 2, 'Mendaftarkan program jaminan sosial', '- Ketentuan dan aturan tentang kepesertaan dan manfaat program jaminan sosial dipahami dengan seksama.\r\n- Pendaftaran kepesertaan program jaminan sosial dilaksanakan dengan melengkapi data perusahaan, data pekerja dan upah pekerja sesuai ketentuan. \r\n- Kartu kepesertaan anggota jaminan sosial dari badan penyelenggara dibagikan kepada pekerja. '),
(4, 2, 'Melakukan administrasi kepesertaan jaminan sosial', '- Pembayaran iuran program jaminan sosial dilakukan sesuai dengan ketentuan yang berlaku.\r\n- Penerimaan manfaat program jaminan sosial dipastikan sampai kepada peserta atau ahli waris yang berhak.\r\n- Pelaksanaan administrasi jaminan sosial didokumentasikan.'),
(5, 3, 'Mengkompilasi ketentuan dan prosedur administrasi pengupahan', '- Ketentuan dan prosedur kerja tentang pengupahan diidentifikasi.\r\n- Ketentuan dan prosedur kerja administrasi pengupahan dikompilasi. '),
(6, 3, 'Melaksanakan administrasi pengupahan', '- Upah dihitung sesuai dengan ketentuan yang berlaku di organisasi.\r\n- Pembayaran upah pekerja dilakukan secara akurat dan tepat waktu sesuai ketentuan yang berlaku.\r\n- Bukti-bukti dan dokumen pelaksanaan pembayaran upah pekerja didokumentasikan.'),
(7, 4, 'Mengkompilasi ketentuan dan prosedur administrasi penerapan kebijakan MSDM', '- Ketentuan dan prosedur kerja berkaitan dengan administrasi penerapan kebijakan MSDM diidentifikasi.\r\n- Prosedur kerja dan bagan alur proses yang berkaitan dengan pengelolaan administrasi penerapan kebijakan MSDM dikompilasi. '),
(8, 4, 'Melaksanakan administrasi kebijakan MSDM', '- Kegiatan administrasi penerapan kebijakan MSDM dilaksanakan secara akurat dan tepat waktu sesuai prosedur kerja yang berlaku.\r\n- Administrasi penerapan kebijakan MSDM didokumentasikan sesuai prosedur.');
